import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import { useAuthentication } from "../util/use-authentication";
import Meta from "../components/Meta";
import BreadCrumb from "../components/BreadCrumb";
import { Button, Col, Container, Image, Row } from "react-bootstrap";
import { Input } from "antd";

const Profile = () => {
  const { id } = useParams();
  const [userData, setUserData] = useState({});
  const { currentUser } = useAuthentication();
  const [isEditing, setIsEditing] = useState(false);
  const [editedData, setEditedData] = useState({});
  useEffect(() => {
    axios
      .get(`https://wdp.bachgiaphat.vn/users/${id}`, {
        headers: { Authorization: `Bearer ${currentUser.accessToken}` },
      })
      .then((response) => {
        setUserData(response.data);
        setEditedData({ ...response.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }, [id]);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleChange = (e) => {
    setEditedData({ ...editedData, [e.target.name]: e.target.value });
  };

  const handleSave = () => {
    axios
      .patch(`https://wdp.bachgiaphat.vn/users/${id}`, editedData)
      .then((response) => {
        setUserData(response.data.data);
        setIsEditing(false);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const handleCancel = () => {
    setEditedData({ ...userData });
    setIsEditing(false);
  };

  return (
    <>
      <Meta title={"Profile"} />
      <BreadCrumb title="Profile" />
      <Container class1="login-wrapper py-5 home-wrapper-2">
        <div className="profile-container">
          <h1 className="text-center">User Profile</h1>
          <Row style={{ padding: "10px" }}>
            <Col xs={3}>
              <Image
                src={
                  userData.image ||
                  "data:image/jpeg;base64,"
                }
                style={{ width: "180px" }}
                rounded
              />
            </Col>
            <Col xs={6}>
              {!isEditing ? (
                <>
                  <p>
                    <strong>Name:</strong> {userData.name}
                  </p>
                  <p>
                    <strong>Email:</strong> {userData.email}
                  </p>
                  <Button variant="primary" onClick={handleEdit}>
                    Edit
                  </Button>
                  
                  <Link to={`/changePassword/${userData._id}`} variant="primary" className="mx-3 btn btn-warning">
                    Change Password
                  </Link>
                </>
              ) : (
                <>
                  <label>
                    <strong>Name:</strong>
                    <Input
                      type="text"
                      name="name"
                      value={editedData.name}
                      onChange={handleChange}
                    />
                  </label>
                  <br />
                  <br />
                  <Button variant="primary" onClick={handleSave}>
                    Save
                  </Button>
                  
                  <Button variant="warning" onClick={handleCancel} className="mx-3">
                    Cancel
                  </Button>
                </>
              )}
            </Col>
          </Row>
        </div>
      </Container>
    </>
  );
};

export default Profile;
